import { Component } from '@angular/core';
import { ApiserviceService } from './apiservice.service';

@Component({
  selector: 'add-file',
  templateUrl: './assignment.component.html',
  styleUrls:['./assignment.component.css']
})
export class Assignmentcomponent { 
    name:any;

    newdata:any;
    constructor(private _apiservice:ApiserviceService){}

    ngOnInit(){
      this._apiservice.getdata().subscribe(res=>{
        this.newdata=res;
      })
    }

    addcontent(){  
        this._apiservice.ut= this.name; 
    }
}
<!--input (input)="update($event)"> -->

<input type="text" placeholder="Write a title" [(ngModel)]="name"/>
<button (click)="addcontent()">Add Title</button> 


<div ng-if="addcontent" class="banner">
     <div class="photo cont">
        <img ng-if="addcontent" src="{{newdata.avatar_url}}" class="profile-pic"  ng-click="userLogin()"/>
        <p>Image</p>  
     </div>
    <div class="cont">
        <span class="name "><h4>{{name}}</h4></span>
        <span class="bio "><p> Bio</p></span>
        <span class="location ">Location</span>
        <span class="socmidea"><p>Twiter :</p></span>
    </div>


</div>
