import { Component } from '@angular/core';
import { ApiserviceService } from './apiservice.service';

@Component({
  selector: 'add-file',
  templateUrl: './assignment.component.html',
  styleUrls:['./assignment.component.css']
})
export class Assignmentcomponent { 
  name:any; 



  newdata:any;
    constructor(private _apiservice:ApiserviceService){}
    ngOnInit(){
      this._apiservice.getdata().subscribe(res=>{
        this.newdata=res;
      })
    }
    addcontent(){
      var a:number = 0;
      while(this.newdata[a].login!= this.name){
         a++;
      }
    }


}
<!--input (input)="update($event)"> -->

<input type="text" placeholder="Write a title" [(ngModel)]="name"/>
<button (click)="addcontent()">Add Title</button> 

{{newdata.name}}
<div ng-if="is_logged_in" class="banner">
     <div class="photo cont">
        <img ng-if="is_logged_in" src="{{newdata.name.avatar_url}}" class="profile-pic"  ng-click="userLogin()"/>
        <svg ng-if="!is_logged_in"  id="Svgpro_out" class="profile-pic" xmlns="http://www.w3.org/2000/svg" width="100" height="100" viewBox="0 0 100 100">
        <path id="Rectangle_21" data-name="Rectangle 21" d="M0,0H100V100H0Z" fill="#5d5b5b"/>
        <g id="5402435_account_profile_user_avatar_man_icon_1" data-name="5402435_account_profile_user_avatar_man_icon (1)" transform="translate(13 15.459)">
        <circle id="Ellipse_3" data-name="Ellipse 3" cx="20" cy="20" r="20" transform="translate(18.374 -1.515)" fill="#fff"/>
        <path id="Path_1" data-name="Path 1" d="M73.624,75.338v4.6a4.6,4.6,0,0,1-4.6,4.6H4.6a4.6,4.6,0,0,1-4.6-4.6v-4.6c0-15.249,12.361-34.61,27.609-34.61H46.015c15.248,0,27.609,19.361,27.609,34.609Z" fill="#fff"/>
        </g>
        </svg>   
     </div>
    <div class="cont">
        <span class="name "><h4>{{name}}</h4></span>
        <span class="bio "><p> Bio</p></span>
        <span class="location ">Location</span>
        <span class="socmidea"><p>Twiter :</p></span>
    </div>


</div>
